mapper.update:
update cardimage
set imagePath=#{imagePath}
where m_id=#{m_id}
and cardNum=#{cardNum}

mapper.select:
select cardnum
from(
select cardNum
from CardImage
where m_id=#{m_id}
order by inputdate desc
)
where rownum = 1

select imagePath
from CardImage
where cardNum=#{cardNum}

mapper.update:
update CardImage
set
shared=#{shared}
where cardNum=#{cardNum}
